import { Transaction, VersionedTransaction } from "@solana/web3.js";
export const loadTransaction = ({ network, txn, isLegacy }) => {
    switch (network) {
        case 'solana': {
            if (isLegacy) {
                return Transaction.from(Buffer.from(txn, "base64"));
            }
            console.log('nolegacy');
            return VersionedTransaction.deserialize(Buffer.from(txn, "base64"));
        }
        default:
            throw new Error(`${network} network is not implemented yet. Please ask to support this network: https://t.me/daniil_jave`);
    }
};
